import React, {useState} from 'react';
import {Button, Card, Col, Drawer, Form, Image, Input, message, Row} from "antd";
import {userLogoutUsingPOST, userUpdateUsingPOST} from "@/services/rico/userController";
import {KeyOutlined, PhoneOutlined, UserOutlined} from "@ant-design/icons";
import {history} from "@@/core/history";
import {stringify} from "querystring";
import {useModel} from "@@/exports";

const Center: React.FC = () => {
  const {initialState} = useModel('@@initialState');
  const [open, setOpen] = useState(false);
  const onfinish = async (values: any) => {
    console.log(values);
    const res = await userUpdateUsingPOST(values);
    if(res.code === 0){
      message.success("修改成功");
      await userLogoutUsingPOST();
      const { search, pathname } = window.location;
      const urlParams = new URL(window.location.href).searchParams;
      const redirect = urlParams.get('redirect');
      if (window.location.pathname !== '/user/login' && !redirect) {
        history.replace({
          pathname: '/user/login',
          search: stringify({
            redirect: pathname + search,
          }),
        });
      }
    }
    else{
      message.error("修改失败，"+res.message);
    }
  }

  return (
    <div className={"center"}>
      <Drawer
        title="修改用户信息"
        placement="left"
        onClose={()=>setOpen(false)} open={open}
        >
        <Form onFinish={(values:API.UserUpdateRequest) => {
          if(!values.nickName) {
            values.nickName = initialState?.currentUser?.nickName;
          }
          if(!values.userPhone) {
            values.userPhone = initialState?.currentUser?.userPhone;
          }
          onfinish(values as API.UserUpdateRequest)
        }} labelAlign="left">
          <Form.Item name="nickName">
            <Input prefix={<UserOutlined/>}  defaultValue={initialState?.currentUser?.nickName}/>
          </Form.Item>
          <Form.Item name="userPhone">
            <Input prefix={<PhoneOutlined/>}  defaultValue={initialState?.currentUser?.userPhone}/>
          </Form.Item>
          <Form.Item name="userNewPassword">
            <Input type="password" prefix={<KeyOutlined/>}  placeholder="输入新的密码"/>
          </Form.Item>
          <Form.Item name="userPassword">
            <Input type="password" prefix={<KeyOutlined/>}  placeholder="输入当前帐号的密码"/>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit">
              提交
            </Button>
          </Form.Item>
        </Form>
      </Drawer>
      <Row gutter={24}>
        <Col span={8}>
          <Card>
            <Row  justify={"center"}>
              <Image
                preview={false}
                width={200}
                height={200}
                src={initialState?.currentUser?.userAvatar}
                fallback="data:image/png;base64,"
              />
            </Row>
            <div style={{marginBottom: 32}}/>
            <Row  justify={"center"}>
              <div style={{fontSize: "24px"}}>{initialState?.currentUser?.nickName}</div>
            </Row>
            <Row justify={"center"}>
              {'账户： ' + initialState?.currentUser?.userAccount}
            </Row>
            <Row justify={"center"}>
              {'联系方式： ' + initialState?.currentUser?.userPhone}
            </Row>
            <div style={{marginBottom: 24}}/>

            <Row align={"middle"} justify={"center"}>
              <Button onClick={()=>setOpen(true)}>
                修改个人信息
              </Button>
            </Row>
          </Card>
        </Col>

        <Col span={8}>
          <Card>

          </Card>
        </Col>
        <Col span={8}>
          <Card>

          </Card>
        </Col>
      </Row>
    </div>
  );
};
export default Center;
